  //       setHabits(JSON.parse(habit))
  //     } catch(e) {
  //       console.log(e)
  //     }
  //   }
  // }, [])

  return (
    <View style={Styles.habit} key={habitId}>
      <Pressable style={Styles.habit_pressable} onPress={addCompletedHabit}>
        <Checkbox style={Styles.checkbox} value={checkMark} onValueChange={setCheckMark} />
        <Text style={Styles.habit_name}>{habitName}</Text>
      </Pressable>
    </View>
  )
}
