  safe_area_view: {
    flex: 1,
  },
  wrapper: {
    flex: 1,
    backgroundColor: 'white',
  },
  container: {
    flex: 1,
    padding: 30,
  },
  top_bar: {
    flexDirection: 'row',
    marginTop: 30,
  },
  page_title: {
    fontSize: 36,
    textAlign: 'center',
    width: '100%',
  },
  checkbox_pressable: {
  },
