import { useState } from 'react';
import { Text, Pressable, View } from 'react-native';
import Styles from './Styles';
import Checkbox from 'expo-checkbox';
import { db, doc, updateDoc, arrayUnion, arrayRemove } from '../firebase/index';

export default function HabitsItem({habitId, habitName, currentDate, isCompleted}) {
  //const [habits, setHabits] = useState([])
  const [checkMark, setCheckMark] = useState(isCompleted)
  const [habitNameStyle, setHabitNameStyle] = useState(Styles.habit_name)

  // ADD COMPLETED HABIT TO STORE, Update STYLING of CHECK MARK and NAME
  const addCompletedHabit = async(habitId) => {
    if(!isCompleted){
      // setHabitNameStyle(Styles.habit_name_completed)
      // setCheckMark(prev => !prev)
      await updateDoc(doc(db, "habits", (habitId)), {
        completed: arrayUnion(currentDate)           
      })
    } else {
      // setHabitNameStyle(Styles.habit_name)
      // setCheckMark(prev => !prev)
      await updateDoc(doc(db, "habits", (habitId)), {
        completed: arrayRemove(currentDate)         
      })
    }
  }

  return (
    <View style={Styles.habit}>
      <Pressable style={Styles.habit_pressable} onPress={() => addCompletedHabit(habitId)}>
        <Checkbox style={Styles.checkbox} value={checkMark} />
        <Text style={isCompleted ? Styles.habit_name : Styles.habit_name_completed}>{habitName}</Text>
      </Pressable>
    </View>
  )
}
